import React, { useState } from 'react';
import './index.css'

const MyAccordian=({id,question,answer})=>{
    const [show,setShow] = useState(false);
    return(
        <>
           <div className='q-div'>
                <p onClick={()=> setShow(!show)}>{ show? '∧':'∨'}</p>
                <h3>{question}</h3>
            </div>
            { show && <p>{answer}</p> }
            
        </>
    )

}
export default MyAccordian
