import React, { useState} from 'react';



const App =()=>{
    let accTime = new Date().toLocaleTimeString();
    const [time ,setTime]=useState(accTime)


    const clickTime=()=>{
       let accRTime = new Date().toLocaleTimeString();  
        setTime(accRTime)
    }

    setInterval(clickTime,1000)

    return(
        <>
            <h1>{time}</h1>
            {/* <button onClick={clickTime}>get time</button> */}
        </>
    )

}
export default App
