import React, { useState} from 'react';
 



const App =()=>{
    const hotColor='yellow';
    const [bg,setBg]=useState(hotColor);
    const [name,setName]=useState('click me')

    const changeBg =()=>{
        setBg('red')
        setName('you clicked me ðŸ˜„')
    }
    
    return(
        <>  <div style={{backgroundColor : bg}}>
               <button onClick={changeBg}>{name}</button>
           </div>
        </>
    )

}
export default App
