import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'

let currTime= new Date(2022,2,7,1);
currTime= currTime.getHours()
let show='';
const cssStyle={};

if(currTime>=1 && currTime<12){
show='good morning';
cssStyle.color='green';
}else if(currTime>=12 && currTime<19){
  show='good evening'
  cssStyle.color='orange';
}else{
  show='good night'
  cssStyle.color='blue';
}

ReactDOM.render(
<div>   <h1>hello sir , <span style={cssStyle}>{show}</span></h1>  </div>  
,document.getElementById('root')
)
