import {Firstname,Lastname} from './App';

const ComC =()=>{
    const fname=useContext(Firstname);
    const lname= useContext(Lastname);
    return (
        <>
            <h1>my name is {fname}{lname}</h1>
        </>
    )

}
export default ComC
