import axios from 'axios';
import React, { useState } from 'react';
import './Ccovid.css'
const Search =()=>{
    const [img,setImg]=useState('');
    const [result,setResult]=useState([]);


     const inputEvent=(e)=>{
        const data=e.target.value
        console.log(data)
        setImg(data)
     }
     const find=()=>{
         axios.get(`https://api.unsplash.com/search/photos?page=1&query=${img}&client_id=ho7ICMUVagq4t8rYruP_15_RUUOhLrYP6Qt3C-utv4M`)
         .then((res)=>{
             console.log(res.data.results)
             setResult(res.data.results)
         })
     }
    return(
        <>
            <div className='search'>
                <input value={img} type='text' placeholder='search here' onChange={inputEvent}></input>
                <button onClick={find}>find</button>
            </div>
            <div className='container'>
                <div className='row text-center text-lg-start col-3'>
                      {result.map((value)=>{
                            return(
                                <img src={value.urls.small}/>
                            )
                      })}

                </div>
            </div>
        </>
    )

}
export default Search
