        console.log(e.target.value)
        console.log(e.target.name)

        const value=e.target.value;
        const name =e.target.name;

        setFullname((prevValue)=>{
            console.log(prevValue)
        })
      }
     

    const onSubmit=(event)=>{
        event.preventDefault();
        alert('from submited')
    }

    return(
        <> 
        <form onSubmit={onSubmit}>
            <div>
            <h1>hello {fullname.fName} {fullname.lName}</h1>
            <input type="text" placeholder='enter full name' name='Fname' onChange={inputEvent} value={fullname.fName}></input>
            <br/>
            <input type="text" placeholder='enter full name' name='lName' onChange={inputEvent} value={fullname.lName}></input>
            <button type='submit'>submit</button>
            </div>
          </form>
        </>
    )

}
export default App
