import React, { useEffect } from 'react';
import './Ccovid.css'
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';


const Covid = ()=>{

    const getCovidData=async ()=>{
        const res=await fetch('https://api.track-covid19.in/reports_v2.json');
        const actualData = await res.json()
        console.log(actualData.states)

    }

   useEffect(()=>{
        getCovidData();
    },[])
    
        return (
            < >
                <div className='container main-div bg-danger'>
                    <div className='center-div'>
                       <h1 className='display-5 mt-5'>indian covid 19 report server</h1>
                    </div>
                    <div className='table-responsive mt-5'>
                        <table className='table table-responsive'>
                            <thead className='thead-info text-center'>
                                <tr >
                                    <th className='th'>state</th>
                                    <th className='th'>confirmed</th>
                                    <th className='th'>recovered</th>
                                    <th className='th'>deaths</th>
                                     <th className='th'>active</th>
                                     <th className='th'>update</th>
                                </tr>
                            </thead>
                        </table>
                    </div>
                </div>
            </>
        )
}
export default Covid
