import React, { useState } from 'react';
import Lists from './Lists';

const App=()=>{
    const [itemList,setItemList]=useState('');
    const [buttonList,setButtonList]=useState([]);
    
    const inputItem =(e) =>{
        setItemList(e.target.value)
    }
    const clickButton = () =>{
     setButtonList((oldItem)=>{
        return[...oldItem,itemList];
    });
    setItemList('')
    };

    const deleteItem=(id)=>{
        console.log('deleted')

        setButtonList((old)=>{
            return old.filter((arrlist,index)=>{
                return index !== id ;
            })
        })
    }

        return(
        <>
            <div className='main-div'>
                <div className='center-div'>
                    <h1>TODO LIST </h1>
                    <br/>
                    <input type='text' placeholder='add item' value={itemList} onChange={inputItem} ></input>
                    <button onClick={clickButton}> + </button>
                    <ol>
                        {
                            buttonList.map( (itemVal,index) =>{
                                return <Lists 
                                text ={itemVal}
                                key={index}
                                id={index}
                                onSelect={deleteItem}
                                />
                            })
                        }

                    </ol>
                </div>
            </div>
        </>
    )
}
export default App
