import React from 'react';

function App(){
    let hours= new Date();
    hours = hours.getHours();
    let print='';
    const style ={ };


    if (hours>=1 && hours<=12){
        print='good morning';
        style.color='green';
    }else if(hours>=13 && hours<19){
        print='good evening';
        style.color='orange';
    }else{
        print='goot night';
        style.color='black';
    }

    return(
        <>
          <h1> hello sir,<span style={style}>{print}</span></h1>
        </>
    )
}

export default App;
