import React, { useState } from 'react';
 

const App =()=>{
    const [fullname,setFullname]=useState({
        fname:'',
        lname:'',
        email:'',
        phone:'',
    });
    
    const inputEvent=(e)=>{
        console.log(e.target.value)
        console.log(e.target.name)

        const value=e.target.value;
        const name =e.target.name;

        setFullname((p)=>{
            // console.log(p);

        if(name==='FName'){
           return{
               fname:value,
               lname:p.lname,
           }
        }else if(name==='LName'){
            return{
                fname:p.fname,
                lname:value,
            }
        }else if (name==='Email'){
            return{
                email:value,
                phone:p.phone,
            }
        }else{
            return{
                email:p.email,
                phone:value,
            }
        }

        });
      };
     

    const onSubmit=(event)=>{
        event.preventDefault();
        alert('from submited')
    }

    return(
        <> 
        <form onSubmit={onSubmit}>
            <div>
            <h1>hello {fullname.fname} {fullname.lname}</h1>
            <h3>{fullname.email}</h3>
            <span>{fullname.phone}</span>
            <br/>

            <input type="text" placeholder='enter full name' name='FName' onChange={inputEvent} 
            value={fullname.fname}
            ></input>

            <br/>

            <input type="text" placeholder='enter full name' name='LName' onChange={inputEvent} 
            value={fullname.lname}
            ></input>

            <br/>

            <input placeholder='inter your email' name='Email' onChange={inputEvent}></input>
            <br/>
            <input type='number' placeholder='inter your phone' name='Phone' onChange={inputEvent}></input>

            <button type='submit'>submit</button>
            </div>
          </form>
        </>
    )

}
export default App
