import React, { useState } from 'react';
import './index.css';
import AddIcon from '@material-ui/icons/Add';
import ExposureNeg1Icon from '@material-ui/icons/ExposureNeg1';
import Button from '@mui/material/Button';

const App= ()=> {
    const [count,setCount]=useState(0);

    const increment =()=>{
          setCount(count +1)
    }
    const decrement =()=>{
        if(count > 0){
        setCount(count -1)
        }else{
            setCount(0)
            alert('its over')
        }
    }

    return(
        <>
            <div className='main-box'>
                <div className='center-box'>
                    <div className='h1'><h1>{count}</h1></div>
                    <br/>
                    <div className='buttons'>
                        <Button className='increment' onClick={increment}> <AddIcon/> </Button>
                        <Button className='decrement' onClick={decrement}><ExposureNeg1Icon/> </Button>
                    </div>
                </div>
            </div>

        </>
    )
}
export default App
