        setItemList(e.target.value)
    }
    const clickButton = (e) =>{
    setButtonList(itemList)
    }
        return(
        <>
            <div className='main-div'>
                <div className='center-div'>
                    <h1>TODO LIST</h1>
                    <br/>
                    <input type='text' placeholder='add item' onChange={inputItem}></input>
                    <button onClick={clickButton}> + </button>
                    <ol>
                    <li>{buttonList}</li>
                    </ol>
                </div>
            </div>
        </>
    )
}
export default App
