import React, { useState } from 'react';
 

const App =()=>{
    const [fullname,setFullname]=useState({
        fname:'',
        lname:'',
    });
    
    const inputEvent=(e)=>{
        console.log(e.target.value)
        console.log(e.target.name)

        const value=e.target.value;
        const name =e.target.name;

        setFullname((p)=>{
            // console.log(p);

        if(name==='FName'){
           return{
               fname:value,
               lname:p.lname,
           }
        }else{
            return{
                fname:p.fname,
                lname:value,
            }
        }

        });
      };
     

    const onSubmit=(event)=>{
        event.preventDefault();
        alert('from submited')
    }

    return(
        <> 
        <form onSubmit={onSubmit}>
            <div>
            <h1>hello {fullname.fname} {fullname.lname}</h1>

            <input type="text" placeholder='enter full name' name='FName' onChange={inputEvent} 
            //value={fullname.fname}
            ></input>

            <br/>

            <input type="text" placeholder='enter full name' name='lName' onChange={inputEvent} 
            //value={fullname.lname}
            ></input>

            <button type='submit'>submit</button>
            </div>
          </form>
        </>
    )

}
export default App
