import React, { useState } from 'react';
import './Ccovid.css'
const Search =()=>{
    const [img,setImg]=useState('');

     const inputEvent=(e)=>{
        const data=e.target.value
        console.log(data)
        setImg(data)
     }
    return(
        <>
            <div className='search'>
                <input value={img} type='text' placeholder='search here' onChange={inputEvent}></input>
            </div>
        </>
    )

}
export default Search
